const runCmd = require('./runCommand');
const getNpm = require('./getNPM');

function install(done) {
  getNpm((npm) => {
    process.stdout.write(`${npm} installing\n`);
    runCmd(npm, ['install'], (c) => {
      process.stdout.write(`${npm} install end`);
      done(c);
    });
  });
}
module.exports = install;
install();
